import React from 'react'

function Search() {
    return render();
}

let dataArray = ["Apples", "Pears", "Watermelons", "Avocado", "Banana", "Cherries", "Apricots", "Plums", "Peaches", "Limes", "Blackberries"];
let input = "";

function search(input) {
    if (input == null || input == "" || input.length <= 0) {
        return null;
    }

    console.log("Called " + input );
    let temp = input.trim();
    const d = dataArray.find(d => d.match(temp));
    return d;
}

function setInput(value) {
    input = value;
}

function render() {
    return (
        <div className='SearchPage'>
            <div className='header'><h2>SEARCH</h2></div>
            <div className="text"><p>This is a data search example.</p></div>
            <hr></hr>
            <div className='search-field'>
                <p>Search data:</p><input type='text' maxLength={32} onChange={e => setInput(e.target.value)}></input>
                <button onClick={alert(input)}>Search</button>
            </div>
        </div>
    )
}

export default Search
