import React, { useState } from 'react'

var dataArray = ["apples", "pears", "watermelons", "avocados", "bananas", "cherries", "apricots", "plums", "peaches", "limes", "blackberries"];
var results = [];

function Search() {
    const [input, setInput] = useState('');
    function search(input) {

        if (input == null || input == "" || input.length <= 0) {
            return null;
        }

        let temp = input.trim();
        dataArray.forEach(element => { if (element.includes(input)) results.push(element) });
        return results;
    }

    const onChangeEvent = (event) => {
        var newValue = event.target.value;
        setInput(newValue);
    }

    const buttonOnClick = () => {
        results = search(input);
    }

    return render(input, onChangeEvent, buttonOnClick);
}

function render(input, onChangeEvent, buttonOnClick) {
    return (
        <div className='SearchPage'>
            <div className='header'><h2>SEARCH</h2></div>
            <div className="text"><p>This is a data search example.</p></div>
            <hr></hr>
            <div className='search-field'>
                <p>Search data:</p><input type='text' maxLength={32} value={input} onChange={onChangeEvent}></input>
                <button onClick={buttonOnClick}>Search</button>
                <div>
                    {/* Display results from the state */}
                    {results.map((res, index) => (
                        <h4 key={index}>{res}</h4>
                    ))}
                </div>
            </div>
        </div>
    )
}

export default Search
