import React, { useState } from 'react'

let dataArray = ["apples", "pears", "watermelons", "avocados", "bananas", "cherries", "apricots", "plums", "peaches", "limes", "blackberries"];

function Search() {
    const [input, setInput] = useState('');
    function search(input) {
        if (input == null || input == "" || input.length <= 0) {
            return null;
        }
    
        let temp = input.trim().toLowerCase();
        var d = dataArray.find(d => d.match(temp));
   
        for(i = 0; i<d.length;++i){
            console.log(d[i]);
        }
        return d;
    }

    const onChangeEvent = (event) => {
        var newValue = event.target.value;
        setInput(newValue);
    }

    const buttonOnClick = () => {
        var result = search(input);
        console.log(result);
    }

    return render(input, onChangeEvent, buttonOnClick);
}

function render(input, onChangeEvent, buttonOnClick) {
    return (
        <div className='SearchPage'>
            <div className='header'><h2>SEARCH</h2></div>
            <div className="text"><p>This is a data search example.</p></div>
            <hr></hr>
            <div className='search-field'>
                <p>Search data:</p><input type='text' maxLength={32} value={input} onChange={onChangeEvent}></input>
                <button onClick={buttonOnClick}>Search</button>
            </div>
        </div>
    )
}

export default Search
